package JavaSessions;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//1.Primitive
		//2.Non-Primitive
		
		//1.Primitive DataType 
		//a.Integral
		
		//-byte: 
		//range: -128 to 127
		//size:1 Byte= 8 bits
		byte b1=127;
		byte b2=2;
		System.out.println(b1);
		
		
		
		//short:
		//size: 2byte =16 bits
		//range:-32768 to 32767
		short s=22;
		short s2=23;
		
		System.out.println(s+s2);
		
		//integer:
		//size: 4 bytes= 32 bits
		//range: -2^31 to 2^31-1
		int i=20;
		int b=-19;
		System.out.println(i);
		
		
		//long:
		//size: 8bytes =64 bits
		//range:-2^63 to 2^63-1
		long l=2;
		long r=34;
		System.out.println(l-r);
		
		//float:
		//size: 4 bytes
		//range:upto 7 decimal points
		float f=0.33f;
		System.out.println(f);
		
		//double:
		//size: 8 bytes
		//range : upto 16 decimal points
		double d=0.56789324332234;
		System.out.println(d);
		
		//char:
		//size:2 bytes
		char c='1';
		char c1='A';
		char c2='#';
		char c3='0';
		
		System.out.println(c1-c3);
		
		//boolean:
		//true or false 
		
		boolean g=false;
		
		System.out.println(c+c2);
		
		
		
	}

}
