package JavaSessions;

public class String_Concatination {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i=10;
int j=20;
String s ="Hello";
String f="world";

System.out.println(i+j);
System.out.println(s+f);
System.out.println(i+s+f+j);
System.out.println(s+i+j+f);
System.out.println(s+f+i+j);
System.out.println(i+j+s+f);

//whenever adding two string it is concatination
//whenever adding two numbers its called arithmetic operations
	}

}
